/** * Created by abhinavdas on 2/11/17. */public class PureStorageSet {    int[] list;    int[] map;    int size;    int count;    PureStorageSet (int size)    {        list = new int[size];        map = new int[size];        int count = 0;        this.size = size;    }    public boolean add (int n)    {        if(n>size-1)       {           return false;       }        map[count] = n;        list[n] = count;        count++;        return true;    }    public boolean delete (int n)    {        if(!contains(n))            return false;        map[list[n]] = map[count];        count--;        return true;    }    public boolean contains (int n)    {        if(list[n]>count)            return false;        if(map[list[n]]==n)            return true;        return false;    }    public void clear()    {        count =0;        return;    }}